{{groovy}}
import groovy.json.JsonOutput;

if (request.query != null) {
    def sg = xwiki.parseGroovyFromPage("Sports.Code.Groovy")
    def json = ""
    try {
     sg.setXWiki(xwiki, xcontext, services)

     def source = sg.loadSource(doc.fullName)
     sg.addSource(source)

     def lastNb = (request.lastNb) ? Integer.parseInt(request.lastNb) : 1;
     def nextNb = (request.nextNb) ? Integer.parseInt(request.nextNb) : 1;
     json = sg.getResults(request.query, source.sport, source.type, lastNb, nextNb)
     def output = JsonOutput.toJson(json);
     print json.text; 
     print "{{{ ${output} }}}"
    } catch (Throwable e) {
      println e.getMessage();
      println e.getStackTrace()
      println """{{{ ${json} }}}"""
    }
    if (request.debug) {
      println """Debug: {{{Â ${sg.debug} }}}"""
    }
} else {
    println """
    
    {{html clean=false}}
    <form action="">
    Query: <input type="text" name="query" />
    <br />Previous results: <input type="text" name="lastNb" value="1" />
    <br />Next results: <input type="text" name="nextNb" value="1" />
    <br /><input type="checkbox" name="save" value="1" /> Save
    <br /><input type="checkbox" name="force" value="1" /> Force
    <br /><input type="checkbox" name="debug" value="1" /> Debug
    <br />
    <input type="submit" value="Search" class="button" />
    </form>
    {{/html}}
    
    """
}
{{/groovy}}
