<script type="text/javascript">
require(['jquery'], function($) {

  function speak(text) {
    $("#results").html($("#results").html() + "<br />" +  text);
    var synth = window.speechSynthesis;
    var utterThis = new SpeechSynthesisUtterance(text);
    utterThis.lang = 'en-US'
    synth.speak(utterThis);
  }

  function go(form, select) {
     var sport = "";
     var competition = "";
     var year = "2019";
     var type = "0";
     var url;
     if (select) {
       var regex = /(.*?)-(.*?)-(.*?)-(.*)/;
       var found = form.sport.value.match(regex);

       sport = found[1].replace("vb", "volley-ball");
       competition = found[2];
       year = found[3];
       type = found[4];
       url = "/xwiki/bin/get/Sports/Code/JSON?outputSyntax=plain&type=" + type + "&year=" + year + "&sport="+ sport + "&competition=" + competition + "&player=" + encodeURIComponent(form.player.value) + "&lastNb=" + form.lastNb.value + "&nextNb=" + form.nextNb.value;
     } else {
       sport = form.sport.value;
       if (form.type.checked)
        type = "1"
       url = "/xwiki/bin/get/Sports/Code/JSON?outputSyntax=plain&type=" + type + "&year=" + year + "&sport="+ sport + "&player=" + encodeURIComponent(form.player.value) + "&lastNb=" + form.lastNb.value + "&nextNb=" + form.nextNb.value;
     }
       speak("Retrieving " + sport + " results for player " + form.player.value);
       if (form.save && form.save.checked)
        url += "&save=1";
       console.log("URL: " + url)
       $("#results").html($("#results").html() + "<br /><a href='" +  url + "'>" + url + "</a>");
       $.getJSON(url, function(result) {
           console.log(result);
           if (!result || !result.text || result.text.trim()=="")
            speak("No results found")
           else
            speak(result.text);
       }).error(function() { speak("An error occured while retrieving results")});
  }
  window.go = go;

  /* if ('serviceWorker' in navigator) {
    window.addEventListener('load', () => {
      navigator.serviceWorker.register("$xwiki.getDocument("Sports.Code.ServiceWorker").getURL("jsx")", { "scope" : "/" })
          .then((reg) => {
            console.log('Service worker registered.', reg);
          });
    });
  } */
});
</script>
